package ru.jmentor.controllers;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.servlet.ModelAndView;@RestControllerpublic class ErrorController {    @GetMapping(value = "/error")    public ModelAndView viewLoginPage(ModelAndView modelAndView) {            modelAndView.setViewName("error");        return modelAndView;    }}