package ru.jmentor.front.filter;import org.springframework.stereotype.Component;import ru.jmentor.front.dto.UserDto;import javax.servlet.*;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;import java.io.IOException;@Componentpublic class AdminFilter implements Filter {    @Override    public void doFilter(ServletRequest servletRequest, ServletResponse servletResponse, FilterChain filterChain) throws IOException, ServletException {        HttpServletRequest request = (HttpServletRequest) servletRequest;        HttpServletResponse response = (HttpServletResponse) servletResponse;        HttpSession session = request.getSession(false);        if (session != null && session.getAttribute("user") != null) {            UserDto userDto = (UserDto) session.getAttribute("user");            if(userDto.getUserRole().contains("ADMIN")){                filterChain.doFilter(request, response);            } else {                response.sendRedirect(request.getContextPath() + "/error");            }        } else {            response.sendRedirect(request.getContextPath() + "/");        }    }}