package ru.jmentor.front.filter;import org.springframework.boot.web.servlet.FilterRegistrationBean;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configurationpublic class FilterConfig {    @Bean    public FilterRegistrationBean<AuthFilter> authFilterFilterRegistrationBean(){        FilterRegistrationBean<AuthFilter> filterRegistrationBean = new FilterRegistrationBean<>();        AuthFilter authFilter = new AuthFilter();        filterRegistrationBean.setFilter(authFilter);        filterRegistrationBean.addUrlPatterns("/userHome");        filterRegistrationBean.setOrder(2);        return filterRegistrationBean;    }    @Bean    public FilterRegistrationBean<AdminFilter> adminFilterFilterRegistrationBean(){        FilterRegistrationBean<AdminFilter> filterRegistrationBean = new FilterRegistrationBean<>();        AdminFilter adminFilter = new AdminFilter();        filterRegistrationBean.setFilter(adminFilter);        filterRegistrationBean.addUrlPatterns("/admin");        filterRegistrationBean.setOrder(1);        return filterRegistrationBean;    }}