package ru.jmentor.front.service;import org.springframework.http.HttpEntity;import org.springframework.http.HttpHeaders;import org.springframework.http.HttpMethod;import org.springframework.http.ResponseEntity;import org.springframework.stereotype.Component;import org.springframework.web.client.RestTemplate;//@Component /// Это пока не работает!!!public class ServerDataClient {    private HttpHeaders headers = new HttpHeaders();    private HttpEntity<String> entity = new HttpEntity<String>(headers);    private RestTemplate restTemplate = new RestTemplate();    static final String URL_LOGIN = "http://localhost/rest/login";    public ResponseEntity userLogin(){        ResponseEntity response = restTemplate.exchange(URL_LOGIN, HttpMethod.POST, entity, String.class);        String result = (String) response.getBody();        return ResponseEntity.ok(result);    }}