package ru.jmentor.repository;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import ru.jmentor.model.User;import java.util.List;@Repositorypublic interface UserRepository extends JpaRepository<User, Long> {    @Query("SELECT user FROM User as user JOIN FETCH user.roles WHERE user.userName=:userName")    User findByUsername(@Param("userName") String userName);    @Query("SELECT user FROM User as user JOIN FETCH user.roles WHERE user.id=:id")    User getById(@Param("id") Long id);    @Query("FROM User as user LEFT JOIN FETCH user.roles ORDER BY user.id")    List<User> findAll();    @Query("FROM User WHERE userName=:userName")    User isExistByUserName(@Param("userName") String userName);}