package ru.jmentor.repository;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.data.repository.query.Param;import org.springframework.stereotype.Repository;import ru.jmentor.model.User;import java.util.List;@Repositorypublic interface UserRepository extends JpaRepository<User, Long> {    @Query("SELECT user FROM User as user JOIN FETCH user.role WHERE user.login=:login")    User findByUserLogin(@Param("login") String login);    @Query("SELECT user FROM User as user JOIN FETCH user.role WHERE user.id=:id")    User getById(@Param("id") Long id);    @Query("FROM User as user LEFT JOIN FETCH user.role ORDER BY user.id")    List<User> findAll();    @Query("FROM User WHERE login=:login")    User isExistByLogin(@Param("login") String login);}