package ru.jmentor.rest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import ru.jmentor.model.User;import ru.jmentor.service.UserService;@RestController@RequestMapping("/rest")public class AdminRestController {    private UserService service;    @Autowired    public void setService(UserService service) { this.service = service; }    @GetMapping    public ResponseEntity<?> usersTable() { return ResponseEntity.ok(service.getAll()); }    @PostMapping    public void adminCreateUser(@RequestBody User user) { service.register(user); }    @DeleteMapping    public void adminDeleteUser(@RequestBody Long id) { service.delete(id); }    @PutMapping    public void adminEditUser(@RequestBody User user) { service.edit(user); }}